    private void sumbActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_sumbActionPerformed
        // TODO add your handling code here:
        String s = firstn.getText();
        String sn = secn.getText();
        double a = Double.parseDouble(s);
        double b = Double.parseDouble(sn);
        double c = a+b;
        reslt.setText(Double.toString(c));
        
        
    }//GEN-LAST:event_sumbActionPerformed

