    private void firstnKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_firstnKeyPressed
        // TODO add your handling code here:
        int key = evt.getKeyCode();
        if(key == 10)
        {
            secn.requestFocus();
        }
    }//GEN-LAST:event_firstnKeyPressed

